== Display multiples issues using a query in report mode ==

{{code}}
{{gitlab id="xwiki" projectId="244" source="query" style="report"}}
milestone=Milestone 1
state=closed
{{/gitlab}}
{{/code}}

{{gitlab id="xwiki" projectId="244" source="query" style="report"}}
milestone=Milestone 1
state=closed
{{/gitlab}}

property.code:
{{groovy}}
import groovy.json.*;

debugStr = ""

def debug(str) {
  debugStr += str + "\n"
}

def getContentFromURL(url, token) {
   try {
      def pageURL = new URL(url)
      def conn = pageURL.openConnection()
      if (token!=null && !token.isEmpty()) {
        conn.setRequestProperty("PRIVATE-TOKEN", token);
      }
      if(conn.responseCode == 200) {
        def jsonSlurper = new JsonSlurper()
        def result = jsonSlurper.parseText(conn.content.text);
        debug("${url}")
        debug("{{{ ${result} }}}")
        return result;
      } else if (conn.responseCode == 401) {
        println "{{error}}" + services.localization.render("gitlab.macros.authfailed") + "{{/error}}"
        return null;
      } else {
        println "{{error}}Response code not 200 accessing ${url}.GitLab instance URL might be incorrect. Code ${conn.responseCode}{{/error}}"
        return null;
      }
    } catch (e) {
      println "{{error}}Exception accessing ${url}. GitLab instance URL might be incorrect.\n${e}{{/error}}"
      return null;
   }
 }

def getProject(projectId) {
  // https://<server>/api/v4/projects/<projectid>
  return getContentFromURL(gitlabapiUrl + "projects/" + projectId, token)
}

def getIssues(issueFilter) {
  // https://<server>/api/v4/projects/<projectid>/issues/?state=opened&milestone=1.0
  return getContentFromURL(gitlabapiUrl + "projects/" + projectId + "/issues/?" +  java.net.URLEncoder.encode(issueFilter, "UTF-8"), token)
}

def getIssue(issueId) {
  // https://<server>/api/v4/projects/<projectid>/issues/<issueId>
  return getContentFromURL(gitlabapiUrl + "projects/" + projectId + "/issues/" + issueId, token)
}

def getIssueNotes(issueId) {
  // https://<server>/api/v4/projects/<projectid>/issues/<issueId>/notes
  return getContentFromURL(gitlabapiUrl + "projects/" + projectId + "/issues/" + issueId + "/notes", token)
}

def getCommit(commitSha) {
  // https://<server>/api/v4/projects/<projectid>/repository/commits/<commitSha>
  return getContentFromURL(gitlabapiUrl + "projects/" + projectId + "/repository/commits/" + commitSha, token)
}

def getDiff(commitSha) {
  // https://<server>/api/v4/projects/<projectid>/repository/commits/<commitSha>/diff
  return getContentFromURL(gitlabapiUrl + "projects/" + projectId + "/repository/commits/" + commitSha + "/diff", token)
}


def getCommittedFiles(issue) {
  def commits = []
  def notes = getIssueNotes(issue.iid)
  for (note in notes) {
   def noteBody = note.body
   if (noteBody.startsWith("mentioned in commit")) {
    def noteText = noteBody.split(" ")
    def commitSha = noteText[noteText.length-1]
    try {
     def commit = getCommit(commitSha)
     def commitDiff = getDiff(commitSha)
     if (request.debug)
       println commit
       for (diff in commitDiff) {
         if (request.debug)
          println "{{{ ${diff} }}}"
       }
       commits.add([ iid: issue.iid, commit: commit, diffs: commitDiff])
    } catch (e) {
      println "Error for commit ${commitSha} ${noteText} ${e}"
    }
   }
  }
  return commits
}

def displayIssue(issue, fields, style) {
   if (request.debug)
  println "{{{ ${issue} }}}"
 if (style=="enum") {
   if ("closed".equals(issue.state.toString()))
    print "--[[${issue.iid}>>url:${issue.web_url}]]-- "
   else
    print "[[${issue.iid}>>url:${issue.web_url}]] "
 } else if (style=="list") {
   if ("closed".equals(issue.state.toString()))
    println "* --[[${issue.iid}>>url:${issue.web_url}]]-- {{{ ${issue.title} }}}"
   else
    println "* [[${issue.iid}>>url:${issue.web_url}]] {{{ ${issue.title} }}}"
 } else if (style=="table") {
    for (field in fields.split(",")) {
       if (field=="id") {
         if ("closed".equals(issue.state.toString()))
           print "|--[[${issue.iid}>>url:${issue.web_url}]]"
         else
           print "|[[${issue.iid}>>url:${issue.web_url}]]"
       } else {
           def value = issue[field]
           if (value==null)
             value = ""
           print "|((( ${value} )))"
       }
     }
     println ""
 } else if (style=="report") {
   if ("closed".equals(issue.state.toString()))
    println "* --[[${issue.iid}>>url:${issue.web_url}]]-- {{{ ${issue.title} }}}"
   else
    println "* [[${issue.iid}>>url:${issue.web_url}]] {{{ ${issue.title} }}}"
   def commits = getCommittedFiles(issue)
   for (commit in commits) {
     println "** [[{{{${commit.commit.title} }}} on ${commit.commit.created_at}>>url:${commit.commit.web_url}]]"
     for (diff in commit.diffs) {
       println "*** [[${diff.new_path}>>url:${baseRepoUrl}${diff.new_path}]]"
     }
   }
 }
}

try {
  def instanceId = xcontext.macro.params.id
  def authobj = xwiki.getDocument("GitLab.Code.Macros.GitLabConfig").getObject("GitLab.Code.Macros.GitLabConfigClass", "id", instanceId)
  token = ""
  if (authobj) {
    token = authobj.getProperty("token").property.value
    url = authobj.getProperty("url").property.value
  }

  if (url==null || url=="" || !url.startsWith("http")) {
    println "{{error}}" + services.localization.render("gitlab.macros.badurl") + "{{/error}}"
  } else {
    projectId =  xcontext.macro.params.projectId
    gitlabapiUrl = url + "/api/v4/";
    gitlabproject = getProject(projectId)
    if (gitlabproject!=null) {
    baseRepoUrl = "${gitlabproject.web_url}/-/blob/${gitlabproject.default_branch}/"

    def style = xcontext.macro.params.style

    def fields = "id,title,state,created_at,closed_at"
    if (style=="table") {
      for (field in fields.split(","))
       print "|= ${field}"
      println ""
    }

    if (xcontext.macro.params.source && xcontext.macro.params.source=="query") {
      def issueFilter = xcontext.macro.content.split("\r\n").join("&");
      def issues = getIssues(issueFilter)
      for (issue in issues) {
        displayIssue(issue, fields, style)
      }
    } else if (xcontext.macro.content && xcontext.macro.content.trim()!="") {
       def issues = xcontext.macro.content
       for (issueId in issues.split(",")) {
         def issue = getIssue(issueId)
         displayIssue(issue, fields, style)
       }
      }
    }
  }
} catch (e) {
  println e;
}

if (request.debug)
  println debugStr;
{{/groovy}}
